<script>
import axios from "axios";

export default {
  name: "LoginBox",
  props:["show"],
  data(){
    return{
          login:true,
          clicked1:false,
          clicked2:false,
          namenotice:false,
          pwdnotice:false,
          mailnotice:false,
          repwdnotice:false,
          name_input:'',
          mail_input:'',
          pwd_input:'',
          repwd_input:'',
          submitting:false,
          submit_img:require("../assets/ico_loading2104474.gif")
    }
  },
  methods:{
    register_btn(){
      this.login=false;
      this.pwd_input='';
      this.name_input='';
      this.namenotice=false;
      this.pwdnotice=false;
      this.mailnotice=false;
      this.repwdnotice=false;
      this.mail_input='';
      this.repwd_input='';
    },
    login_btn(){
      this.pwd_input='';
      this.name_input='';
      this.login=true;
      this.namenotice=false;
      this.pwdnotice=false;
    },
    submit_inform(){
      var _this=this;
      if(!_this.namenotice&&!_this.pwdnotice){
        _this.submitting=true;
        const btn=this.$refs.submit_btn;
          btn.disabled=true;
          let params={
            uname:_this.name_input,
            upwd:_this.pwd_input
          }
          axios.post("/api/login",params)
            .then(response=>{
              if(response.ok==1) {
                _this.submitting = false;
                btn.disabled = false;
                this.chang_box();
                this.$emit("login_status",this.name_input);
                this.$message({
                  message: '登录成功',
                  type: 'success',
                });
              }else{
                _this.submitting = false;
                btn.disabled = false;
                this.$message({
                  message: '账号或密码错误！',
                  type: 'error'
                });
              }
            }).catch(error=>{
          this.$message({
            message: '服务器错误，请稍后再试!',
            type: 'error'
          });
          _this.submitting=false;
          btn.disabled=false;
        });
      }else {
        this.$message({
          message: '请将信息输入完整！',
          type: 'error'
        });
      }
    },
    submit_inform_register(){
      var _this=this;
      if(!_this.namenotice&&!_this.pwdnotice&&!_this.repwdnotice&&!_this.mailnotice){
        _this.submitting=true;
        const btn=this.$refs.submit_btn;
        btn.disabled=true;
        let params={
          uname:_this.name_input,
          upwd:_this.pwd_input,
          mail:_this.mail_input
        }
        axios.post("/api/register",params)
            .then(response=>{
              if(response.data.ok==1) {
                _this.submitting = false;
                btn.disabled = false;
                this.login_btn();
                this.$message({
                  message: '注册成功，请登录！',
                  type: 'success'
                });
              }else{
                _this.submitting = false;
                btn.disabled = false;
                this.$message({
                  message: '该账号已存在',
                  type: 'error'
                });
              }
            }).catch(error=>{
          this.$message({
            message: '服务器错误，请稍后再试!',
            type: 'error'
          });
          _this.submitting=false;
          btn.disabled=false;
        });
      }else {
        this.$message({
          message: '请将信息输入完整！',
          type: 'error'
        });
      }
    },
    name_notice(){
      var regEn = /[`~!@#$%^&*()_+<>?:"{},.\/;'[\]]/im,
          regCn = /[·！#￥（——）：；“”‘、，|《。》？、【】[\]]/im;
      if(regCn.test(this.pwd_input)||regEn.test(this.pwd_input))
      {
        this.namenotice=true;
      }else {
        this.namenotice=false;
      }

      if(this.name_input==''||this.name_input.length<6){
        this.namenotice=true;
      }else{
        this.namenotice=false;
      }
    },
    pwd_notice(){
      var regEn = /[`~!@#$%^&*()_+<>?:"{},.\/;'[\]]/im,
          regCn = /[·！#￥（——）：；“”‘、，|《。》？、【】[\]]/im;
      if(regCn.test(this.pwd_input)||regEn.test(this.pwd_input))
      {
        this.pwdnotice=true;
      }else {
        this.pwdnotice=false;
      }
      if(this.pwd_input==''||this.pwd_input.length<6){
        this.pwdnotice=true;
      }else{
        this.pwdnotice=false;
      }
    },
    repwd_notice(){
     if (this.pwd_input!=this.repwd_input){
       this.repwdnotice=true;
     }else{
       this.repwdnotice=false;
     }
    },
   mail_notice(){
     var reg = /^([a-zA-Z]|[0-9])(\w|\-)+@[a-zA-Z0-9]+\.([a-zA-Z]{2,4})$/;
     if(reg.test(this.mail_input)){
       this.mailnotice=false;
     }else{
       this.mailnotice=true;
     }
    },
    chang_box(){
      let el1 = event.currentTarget
      let el2 = event.target
      if (el1 === el2) {
        this.$emit("update:show",false);
        this.$emit("disable_scroll",false);
      }

    }
  }

}
</script>

